const initialState = {
    items: [],
    loading: false

}

export default function(state = initialState, action) {
    switch(action.type) {
        case 'GET_ARTISTS':
            return {
                ...state,
                items: action.payload,
                loading: false
            }
        // case 'ADD_ARTIST':
        //     return {
        //         ...state,
        //         artists: [action.payload, ...state.artists]
        //      }
        default:
            return state
    }
}
