import { ADD_ARTIST } from "./reducers"

export const addArtist = (artist) => {
    console.log(artist)
    return (dispatch) => {
        fetch("http://localhost:5000/api/artists", {
            method:"post",
            headers:{"Content-Type":"application/json"},
            mode:"no-cors",
            body:JSON.stringify({
                artist:artist
            })
        })
        .then(res => res.json())
        .then(response => {
            console.log("response", response)
            const action={
                type: 'ADD_ARTIST',
                value: response
            }
            dispatch(action)
        })
    }
}



