export const addArtist = async(url, artists) => {
    let response = await fetch(url, artists)
    let json = await response.json()
    return(dispatch) => {
        console.log(json)
        const action={
            type: 'ADD_ARTIST',
            value: json
        }
    dispatch(action)
    }
}
